$("#contract").ajaxSubmit({
    dataType: "json",
    async: false,
    beforeSubmit: function (formData, jqForm, options) {
        tipIndex = parent.layer.load(1, {
            shade: 0.01
        });
    },
    success: function (ret) {
        if (ret.state == "ok") {
            parent.layer.msg(ret.msg, {
                time: 3000,
            })

        } else {
            layer.msg(ret.msg, {
                time: 3000,
            })
        }

    },
    complete: function (ret) {
        parent.layer.close(tipIndex);
    }
})

